mapper.update:

UPDATE notification
SET mod_user		= #{modUser}
	,mod_dttm		= #{modDate}
	,notice_title	= #{noticeTitle}
	,notice_content = #{noticeContent}
WHERE notice_seq = #{noticeSeq}


UPDATE file_detail_info
SET file_server_nm = #{fileDetailServerName, jdbcType=VARCHAR}
WHERE file_seq = #{fileId}


UPDATE notification
SET views = views + 1
WHERE notice_seq = #{noticeSeq}
