mapper.select:
SELECT ROW_NUM AS "rowNum", USER_TYPE AS "userType", USER_ID AS "userId", USER_NM AS "userName", USER_TELNO AS "userPhoneNumber", STORE_SEQ AS "storeId", STORE_NM AS "storeName", STORE_TELNO AS "storePhoneNumber", LOCATION_CD AS "storeLocation", SCHEDULE_DATE AS "scheduleDate", SCHEDULE_TYPE AS "scheduleType", SCHEDULE_ID AS "scheduleId"
FROM( SELECT ROW_NUMBER() OVER(ORDER BY SCHEDULE_DATE) AS ROW_NUM, subquery.* FROM( SELECT '기사' AS "USER_TYPE", u."USER_ID", u."USER_NM", u."USER_TELNO", su."STORE_SEQ", s."STORE_NM", s."STORE_TELNO", s."LOCATION_CD", aa."VISIT_DTTM" AS SCHEDULE_DATE, '배정' AS "SCHEDULE_TYPE", aa."AS_ASSIGN_SEQ" AS SCHEDULE_ID FROM AS_ASSIGN aa LEFT OUTER JOIN USERS u ON aa.mechanic_id = u.user_id LEFT OUTER JOIN AS_INFORMATION ai ON aa."AS_INFO_SEQ" = ai."AS_INFO_SEQ" LEFT OUTER JOIN STORE_USER_MAP su ON ai."STORE_MNG_ID" = su."USER_ID" LEFT OUTER JOIN STORE s ON su."STORE_SEQ" = s."STORE_SEQ"
UNION ALL
SELECT
CASE WHEN h."GRP_CD_DTL_ID" = '02' THEN '점주' WHEN h."GRP_CD_DTL_ID" = '03' THEN '기사' ELSE NULL END AS "USER_TYPE", h."USER_ID", u."USER_NM", u."USER_TELNO", s."STORE_SEQ", st."STORE_NM", st."STORE_TELNO", st."LOCATION_CD", h."HOLIDAY" AS SCHEDULE_DATE, '휴무' AS "SCHEDULE_TYPE", h."HOLIDAY_SEQ" AS SCHEDULE_ID FROM "HR"."HOLIDAY" h LEFT JOIN "HR"."STORE_USER_MAP" s ON h."USER_ID" = s."USER_ID" LEFT JOIN "HR"."USERS" u ON h."USER_ID" = u."USER_ID" LEFT JOIN "HR"."STORE" st ON s."STORE_SEQ" = st."STORE_SEQ"
UNION ALL
SELECT '기사' AS "USER_TYPE", u."USER_ID", u."USER_NM", u."USER_TELNO", su."STORE_SEQ", s."STORE_NM", s."STORE_TELNO", s."LOCATION_CD", aa."VISIT_DTTM" AS SCHEDULE_DATE, '근무' AS "SCHEDULE_TYPE", ar."AS_RESULT_SEQ" AS SCHEDULE_ID FROM AS_RESULT ar LEFT OUTER JOIN AS_ASSIGN aa ON ar."AS_ASSIGN_SEQ" = aa."AS_ASSIGN_SEQ" LEFT OUTER JOIN USERS u ON aa.mechanic_id = u.user_id LEFT OUTER JOIN AS_INFORMATION ai ON aa."AS_INFO_SEQ" = ai."AS_INFO_SEQ" LEFT OUTER JOIN STORE_USER_MAP su ON ai."STORE_MNG_ID" = su."USER_ID" LEFT OUTER JOIN STORE s ON su."STORE_SEQ" = s."STORE_SEQ") subquery
WHERE 1=1
AND ( EXTRACT(YEAR FROM SCHEDULE_DATE) > #{searchContent.startYr} OR (EXTRACT(YEAR FROM SCHEDULE_DATE) = #{searchContent.startYr} AND EXTRACT(MONTH FROM SCHEDULE_DATE) >= #{searchContent.startMn}) )
AND ( EXTRACT(YEAR FROM SCHEDULE_DATE) < #{searchContent.endYr} OR (EXTRACT(YEAR FROM SCHEDULE_DATE) = #{searchContent.endYr} AND EXTRACT(MONTH FROM SCHEDULE_DATE) <= #{searchContent.endMn}) )
AND USER_ID = #{searchContent.userId}
AND USER_TYPE = #{searchContent.userType}
AND USER_NM = #{searchContent.userName}
AND STORE_SEQ = #{searchContent.storeId}
AND STORE_NM = #{searchContent.storeName}
AND SCHEDULE_TYPE = #{searchContent.scheduleType}
)
WHERE ROW_NUM BETWEEN #{firstRowNum} AND #{lastRowNum}

SELECT COUNT(*) FROM( SELECT '기사' AS "USER_TYPE", u."USER_ID", u."USER_NM", u."USER_TELNO", su."STORE_SEQ", s."STORE_NM", s."STORE_TELNO", s."LOCATION_CD", aa."VISIT_DTTM" AS SCHEDULE_DATE, '배정' AS "SCHEDULE_TYPE", aa."AS_ASSIGN_SEQ" AS SCHEDULE_ID FROM AS_ASSIGN aa LEFT OUTER JOIN USERS u ON aa.mechanic_id = u.user_id LEFT OUTER JOIN AS_INFORMATION ai ON aa."AS_INFO_SEQ" = ai."AS_INFO_SEQ" LEFT OUTER JOIN STORE_USER_MAP su ON ai."STORE_MNG_ID" = su."USER_ID" LEFT OUTER JOIN STORE s ON su."STORE_SEQ" = s."STORE_SEQ"
