mapper.select:

SELECT user_nm
FROM users
WHERE user_id = #{writetId}

mapper.update:

UPDATE notification
SET mod_user		= #{modUser}
	,mod_dttm		= SYSDATE
	,notice_title	= #{noticeTitle}
	,notice_content = #{noticeContent}
WHERE notice_seq = #{noticeSeq}


UPDATE notification
SET views = views + 1
WHERE notice_seq = #{noticeSeq}
