mapper.select:
SELECT * FROM (SELECT ROWNUM AS ROW_NUMBER, subquery.* FROM ( SELECT gc.GRP_CD_DTL_NM AS USER_TYPE, users.USER_ID, users.USER_NM, users.USER_TELNO, store.STORE_SEQ, STORE.STORE_NM, STORE.STORE_TELNO, CASE WHEN gc.GRP_CD_DTL_NM = '기사' THEN users.LOCATION_CD WHEN gc.GRP_CD_DTL_NM = '점주' THEN STORE.LOCATION_CD ELSE NULL END AS LOCATION_CD, holiday.HOLIDAY_SEQ AS SCHEDULE_ID, holiday.HLDY_APP, holiday.HLDY_STAR AS START_DATE, holiday.HLDY_END AS END_DATE, holiday.ACTL_USE FROM holiday LEFT OUTER JOIN users ON holiday.USER_ID = users.USER_ID LEFT OUTER JOIN GROUP_CODE_DETAIL gc ON users.USER_TYPE_CD = gc.GRP_CD_DTL_ID LEFT OUTER JOIN STORE_USER_MAP sum ON users.USER_ID = sum.USER_ID LEFT OUTER JOIN STORE ON sum.STORE_SEQ = STORE.STORE_SEQ WHERE gc.GRP_CD_ID = 'U' ORDER BY START_DATE DESC ) subquery
WHERE 1=1 and ACTL_USE='Y'
AND ( (YEAR(START_DATE) * 10000 + MONTH(START_DATE) * 100 + DAY(START_DATE)) BETWEEN (#{searchContent.startYr} * 10000 + #{searchContent.startMn} * 100 + 01) AND (#{searchContent.endYr} * 10000 + #{searchContent.endMn} * 100 + 31) )
AND USER_ID = #{searchContent.userId}
AND USER_TYPE = #{searchContent.userType}
AND USER_NM = #{searchContent.userName}
AND STORE_SEQ = #{searchContent.storeId}
AND STORE_NM = #{searchContent.storeName}
AND SCHEDULE_TYPE = #{searchContent.scheduleType} )
WHERE ROW_NUMBER BETWEEN #{firstRowNum} AND #{lastRowNum}

SELECT COUNT(*) FROM ( SELECT ROWNUM AS ROW_NUMBER, subquery.* FROM ( SELECT gc.GRP_CD_DTL_NM AS USER_TYPE, users.USER_ID, users.USER_NM, users.USER_TELNO, store.STORE_SEQ, STORE.STORE_NM, STORE.STORE_TELNO, CASE WHEN gc.GRP_CD_DTL_NM = '기사' THEN users.LOCATION_CD WHEN gc.GRP_CD_DTL_NM = '점주' THEN STORE.LOCATION_CD ELSE NULL END AS LOCATION_CD, holiday.HOLIDAY_SEQ AS SCHEDULE_ID, holiday.HLDY_APP, holiday.HLDY_STAR AS START_DATE, holiday.HLDY_END AS END_DATE, holiday.ACTL_USE FROM holiday LEFT OUTER JOIN users ON holiday.USER_ID = users.USER_ID LEFT OUTER JOIN GROUP_CODE_DETAIL gc ON users.USER_TYPE_CD = gc.GRP_CD_DTL_ID LEFT OUTER JOIN STORE_USER_MAP sum ON users.USER_ID = sum.USER_ID LEFT OUTER JOIN STORE ON sum.STORE_SEQ = STORE.STORE_SEQ WHERE gc.GRP_CD_ID = 'U' ORDER BY START_DATE DESC ) subquery WHERE 1=1 and ACTL_USE='Y'
AND ( (YEAR(START_DATE) * 10000 + MONTH(START_DATE) * 100 + DAY(START_DATE)) BETWEEN (#{searchContent.startYr} * 10000 + #{searchContent.startMn} * 100 + 01) AND (#{searchContent.endYr} * 10000 + #{searchContent.endMn} * 100 + 31) )
AND USER_ID = #{searchContent.userId}
AND USER_TYPE = #{searchContent.userType}
AND USER_NM = #{searchContent.userName}
AND STORE_SEQ = #{searchContent.storeId}
AND STORE_NM = #{searchContent.storeName}
AND SCHEDULE_TYPE = #{searchContent.scheduleType}
)

select USER_ID from users
where LOCATION_CD LIKE '%' ||
#{locationCd} ||
'%'

	AND USER_ID = #{userId}


	AND USER_NM = #{userNm}


	AND EQPMN_CD = #{eqpmnCd}
